import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: []
})
export class DashboardComponent {

}
<!DOCTYPE html>
<html lang="es">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Transforma tus ideas en Realidad digital</title>
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css">
  <style>
    body {
      font-family: Arial, sans-serif;
    }

    main {
      display: flex;
      flex-direction: column;
      align-items: center;
      padding: 20px;
    }

    .container {
      max-width: 800px;
      margin: 40px auto;
      padding: 20px;
      background-color: #f9f9f9;
      border: 1px solid #ddd;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    }

    .row {
      display: flex;
      flex-wrap: wrap;
      align-items: center;
    }

    .text-section {
      padding: 20px;
    }

    .image-section {
      text-align: center;
      padding: 20px;
    }

    .image-section img {
      width: 100%;
      height: 200px;
      object-fit: cover;
      border-radius: 10px;
    }

    .col-12 {
      margin-top: 40px;
    }

    .col-md-3 {
      padding: 10px;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      text-align: center;
    }

    .col-md-3 img {
      width: 100%;
      height: 150px;
      object-fit: cover;
      border-radius: 10px;
    }

    .cta {
      background-color: #4CAF50;
      color: #fff;
      padding: 10px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      margin-top: 10px;
    }

    .cta:hover {
      background-color: #3e8e41;
    }
  </style>
</head>

<body>
  <main>
    <div class="container">
      <div class="row">
        <div class="text-section col-12 col-md-6">
          <h2>Transforma tus ideas en Realidad digital</h2>
          <p>Soy Lucia, una desarrolladora web que cesa modernos y funcionales, estableciendo presencia en la web con una combinación de creatividad y habilidad.</p>
          <a href="#" class="cta">Leer más</a>
        </div>
        <div class="image-section col-12 col-md-6">
          <img src="https://via.placeholder.com/500/666666?text=IMAGEN" alt="Imagen representativa">
        </div>
      </div>
    </div>
    <div class="col-12">
      <div class="row">
        <div class="col-md-3 col-6">
          <img src="https://via.placeholder.com/150/333333/FFFFFF?text=EJEMPLO" alt="EJEMPLO">
        </div>
        <div class="col-md-3 col-6">
          <img src="https://via.placeholder.com/150/666666?text=IDEJEMPLO" alt="IDEJEMPLO">
        </div>
        <div class="col-md-3 col-6">
          <img src="https://via.placeholder.com/150/999999?text=EJEMPLO" alt="EJEMPLO">
        </div>
        <div class="col-md-3 col-6">
          <h2>La guía definitiva de escritura</h2>
          <p>La creación de sitios web eficaces es un arte. Me encargo de cada proyecto con pasión, aplicando técnicas adecuadas para la plataforma.</p>
          <a href="#" class="cta">Leer más</a>
        </div>
      </div>
    </div>
  </main>
</body>

</html>
