// navbar.component.ts
import { Component, Input, OnInit, OnDestroy } from '@angular/core';
import { SidebarService } from 'src/app/public/services/sidebar.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html'
})
export class NavbarComponent implements OnInit, OnDestroy {
  @Input() pageTitle: string = '';
  private sidebarSubscription: Subscription = new Subscription();

  constructor(private sidebarService: SidebarService) {}

  ngOnInit(): void {
    this.sidebarSubscription = this.sidebarService.getSidebarStatus().subscribe();
  }

  toggleSidebar() {
    this.sidebarService.toggleSidebar();
  }

  ngOnDestroy(): void {
    this.sidebarSubscription.unsubscribe();
  }
}
<!-- navbar.component.html -->
<nav class="navbar navbar-dark" style="background: linear-gradient(to right, #FFB58A, #FF0065); height: 40px;">
  <div class="container-fluid">
    <div class="d-flex justify-content-between w-100">
      <button class="navbar-toggler" type="button" (click)="toggleSidebar()">
        <span class="navbar-toggler-icon"></span>
      </button>
      <span class="navbar-text ms-2">{{ pageTitle }}</span>
      <form class="d-flex flex-grow-1 justify-content-end me-5">
        <input class="form-control me-2" type="search" placeholder="Escribe aquí" aria-label="Search" style="max-width: 300px;">
        <button class="btn btn-outline-light" type="submit">Buscar</button>
      </form>
    </div>
  </div>
</nav>
