// navbar.component.ts
import { Component, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html'
})
export class NavbarComponent {
  @Output() toggleSidebarEvent = new EventEmitter<void>();

  toggleSidebar() {
    this.toggleSidebarEvent.emit();
  }
}
<!-- navbar.component.html -->
<nav class="navbar navbar-dark" style="background: linear-gradient(to right, #FFB58A, #FF0065);">
  <div class="container-fluid">
    <div class="d-flex justify-content-between w-100">
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarToggleExternalContent" aria-controls="navbarToggleExternalContent" aria-expanded="false" aria-label="Toggle navigation" (click)="toggleSidebar()">
        <span class="navbar-toggler-icon"></span>
      </button>
      <form class="d-flex flex-grow-1 justify-content-end me-10">
        <input class="form-control me-2" type="search" placeholder="Escribe aqui" aria-label="Search" style="max-width: 300px;">
        <button class="btn btn-outline-light" type="submit">Buscar</button>
      </form>
    </div>
  </div>
</nav>
